import { Component } from '@angular/core';
import { Gasto } from 'src/app/models/gasto';
import { GastoService } from 'src/app/services/gasto.service';
import { NgForm } from '@angular/forms';

@Component({
  selector: 'app-gastos',
  templateUrl: './gastos.component.html',
  styleUrls: ['./gastos.component.css']
})
export class GastosComponent {

  constructor(protected gastoService: GastoService) { }

  ngOnInit(): void {
    this.getGastos();
  }

  resetForm(form?: NgForm) {
    if (form) {
      form.reset();
      this.gastoService.selectedGasto = new Gasto();
    }
  }

  addGasto(form: NgForm) {
    this.gastoService.postGasto(form.value)
      .subscribe(res => {
        console.log(res);
        this.resetForm(form);
        //M.toast({ html: 'Gasto Guardado' });
      })
  }

  getGastos() {
    this.gastoService.getGastos()
      .subscribe(res => {
        this.gastoService.gastos = res as Gasto[];
        console.log(res);
      })
  }

  editGasto(gasto: Gasto) {
    // Asignamos el gasto seleccionado al servicio para que esté disponible en otras partes del componente.
    this.gastoService.selectedGasto = Object.assign({}, gasto);
  }

  deleteGasto(gastoId: string) {
    if (confirm('¿Está seguro de que desea eliminar este gasto?')) {
      this.gastoService.deleteGasto(gastoId)
        .subscribe(res => {
          // Filtramos el gasto eliminado del arreglo de gastos en el servicio.
          this.gastoService.gastos = this.gastoService.gastos.filter(gasto => gasto._id !== gastoId);
          //M.toast({ html: 'Gasto Eliminado' });
        });
    }
  }
}
