import { Component } from '@angular/core';
import { Gasto } from 'src/app/models/gasto';
import { GastoService } from 'src/app/services/gasto.service';
import { NgForm } from '@angular/forms';

@Component({
  selector: 'app-gastos',
  templateUrl: './gastos.component.html',
  styleUrls: ['./gastos.component.css']
})
export class GastosComponent {

  constructor(protected gastoService: GastoService) { }

  ngOnInit(): void {
    this.getGastos();
  }

  resetForm(form?: NgForm) {
    if (form) {
      form.reset();
      this.gastoService.selectedGasto = new Gasto();
    }
  }

  addGasto(form: NgForm) {
    this.gastoService.postGasto(form.value)
      .subscribe(res => {
        console.log(res);
        this.resetForm(form);
        this.getGastos();
        //M.toast({ html: 'Gasto Guardado' });
      })
  }

  getGastos() {
    this.gastoService.getGastos().subscribe(res => {
        this.gastoService.gastos = res as Gasto[];
        console.log(res);
      })
  }

  editGastoForm(gasto: Gasto) {
    // const editedGasto = { ...form.value }; // Create a copy of the form value to avoid changing the selectedGasto before deleting it.

    // this.gastoService.deleteGasto(editedGasto._id).subscribe(() => {
    //   this.gastoService.getGastos().subscribe(res => {
    //     this.gastoService.gastos = res as Gasto[];
    //     console.log(res);

    //     // Find the index of the editedGasto in the gastos array.
    //     const index = this.gastoService.gastos.findIndex(gasto => gasto._id === editedGasto._id);

    //     if (index !== -1) {
    //       this.gastoService.gastos.splice(index, 1); // Remove the old version of the gasto from the gastos array.
    //       this.gastoService.gastos.push(form.value); // Push the edited gasto to the gastos array.
    //     }

    //     this.resetForm(form);
    //   })
    // });
    this.gastoService.selectedGasto = { ...gasto };
  }

  deleteGasto(gastoId: string) {
    if (confirm('¿Está seguro de que desea eliminar este gasto?')) {
      this.gastoService.deleteGasto(gastoId).subscribe(() => {
        this.gastoService.getGastos().subscribe(res => {
          this.gastoService.gastos = res as Gasto[];
          console.log(res);
        })
      });
    }
  }
}
